import React from "react";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";

import RentalCategory from "../../components/rental-category/rental-category";

import { selectRentalsForPreview } from "../../redux/rental/rental.selectors";

import {
  firestore,
  converCollectionsShanpshotToMap,
} from "../../firebase/firebase.utils";

class RentalPage extends React.Component {
  unsubscribeFromShampshort = null;

  componentDidMount() {
    const rentalRef = firestore.collection("rentals");
    rentalRef.onSnapshot(async (snapshot) => {
      converCollectionsShanpshotToMap(snapshot);
    });
  }

  render() {
    const { rentals } = this.props;
    return (
      <div className="rental-page background">
        {rentals.map(({ categoryID, ...otherRentalProps }) => (
          <RentalCategory key={categoryID} {...otherRentalProps} />
        ))}
      </div>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  rentals: selectRentalsForPreview,
});

export default connect(mapStateToProps)(RentalPage);
