import React, { useRef, useState } from "react";
import { Link } from "react-router-dom";
// higher order component that let's us modify our component to have acces
// to things related to Redux
import { connect } from "react-redux";

import SignInDropdown from "../sign-in-dropdown/sign-in-dropdown.component";
import CartDropdown from "../cart-dropdown/cart-dropdown.component";
import CartIcon from "../cart-icon/cart-icon.component";
import { auth } from "../../firebase/firebase.utils";

import { useDetectOutsideClick } from "./useDetectOutsideClick";

import {
  selectCurrentUser,
  selectUserSignInHidden,
} from "../../redux/user/user.selectors";

import { selectCartHidden } from "../../redux/cart/cart.selectors";
import { toggleUserSignInHidden } from "../../redux/user/user.actions";

import Logo from "../../assets/svg/logo.svg";

import { createStructuredSelector } from "reselect";
import HomeIcon from "../../assets/svg/home.svg";
import ShopIcon from "../../assets/svg/shop.svg";
import RentIcon from "../../assets/svg/snowflake-o.svg";
import ContactIcon from "../../assets/svg/phone.svg";
import ResturantIcon from "../../assets/svg/spoon-knife.svg";
import SignInIcon from "../../assets/svg/enter.svg";

function Header({
  currentUser,
  userSignInHidden,
  cartHidden,
  toggleUserSignInHidden,
}) {
  const [smallScreen, setScreen] = useState(document.documentElement.clientWidth < 600);

  const dropdownRef = useRef(null);
  const [isActive, setIsActive] = useDetectOutsideClick(dropdownRef, false);
  const onClick = () => setIsActive(!isActive);

  const setScreenSize = () => {
    setScreen(document.documentElement.clientWidth < 600);
  };

  window.addEventListener("resize", setScreenSize);

  return (
    <div className="header">
      <Link className="header__logo-container" to="/">
        <img src={Logo} className="header__logo" alt="logo" />
      </Link>

      {smallScreen ? (

          <div className="menu-container">
            <button onClick={onClick} className="menu-trigger">
              <span>{currentUser}</span>
              {/* <img
                src="https://steamcdn-a.akamaihd.net/steamcommunity/public/images/avatars/df/df7789f313571604c0e4fb82154f7ee93d9989c6.jpg"
                alt="User avatar"
              /> */}
            </button>
            <nav
              ref={dropdownRef}
              className={`menu ${isActive ? "active" : "inactive"}`}
            >
              <ul>
                <li>
                  <Link className="header__options--option" to="/">
                    <img
                      src={HomeIcon}
                      alt="Home Icon"
                      className="header__icon"
                    />
                    HOME
                  </Link>
                </li>

                <li>
                  <Link className="header__options--option" to="/restaurant">
                    <img
                      src={ResturantIcon}
                      alt="Resturant Icon"
                      className="header__icon"
                    />
                    RESTAURANT
                  </Link>
                </li>

                <li>
                  <a
                    className="header__options--option"
                    href="https://www.tracksport.ro/"
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    <img
                      src={ShopIcon}
                      alt="Shop Icon"
                      className="header__icon"
                    />
                    SHOP
                  </a>
                </li>

                <li>
                  <Link className="header__options--option" to="/rental">
                    <img
                      src={RentIcon}
                      alt="Rent Icon"
                      className="header__icon"
                    />
                    RENTAL
                  </Link>
                </li>

                <li>
                  <Link className="header__options--option" to="/contact">
                    <img
                      src={ContactIcon}
                      alt="Contact Icon"
                      className="header__icon"
                    />
                    CONTACT
                  </Link>
                </li>

                <li>
                  {currentUser ? (
                    <div
                      className="header__options--option"
                      onClick={() => auth.signOut()}
                    >
                      <div className="header__options--user">
                        <span className="header__options--user header__options--user__visible">
                          Logged in as{" "}
                          {currentUser.firstName + " " + currentUser.lastName}
                        </span>
                        <span className="header__options--user header__options--user__invisible">
                          SIGN OUT
                        </span>
                      </div>
                    </div>
                  ) : (
                    <Link
                      className="header__options--option header__options--option--SignIn"
                      onClick={toggleUserSignInHidden}
                    >
                      <img
                        src={SignInIcon}
                        alt="SignIn Icon"
                        className="header__icon"
                      />
                      SIGN IN / REGISTER
                    </Link>
                  )}
                </li>
              </ul>
            </nav>
          </div>

      ) : (
        <div className="header__options">
          <Link className="header__options--option" to="/">
            <img src={HomeIcon} alt="Home Icon" className="header__icon" />
            HOME
          </Link>

          <Link className="header__options--option" to="/restaurant">
            <img
              src={ResturantIcon}
              alt="Resturant Icon"
              className="header__icon"
            />
            RESTAURANT
          </Link>

          <a
            className="header__options--option"
            href="https://www.tracksport.ro/"
            target="_blank"
            rel="noopener noreferrer"
          >
            <img src={ShopIcon} alt="Shop Icon" className="header__icon" />
            SHOP
          </a>

          <Link className="header__options--option" to="/rental">
            <img src={RentIcon} alt="Rent Icon" className="header__icon" />
            RENTAL
          </Link>

          <Link className="header__options--option" to="/contact">
            <img
              src={ContactIcon}
              alt="Contact Icon"
              className="header__icon"
            />
            CONTACT
          </Link>

          {currentUser ? (
            <div
              className="header__options--option"
              onClick={() => auth.signOut()}
            >
              <div className="header__options--user">
                <span className="header__options--user header__options--user__visible">
                  Logged in as{" "}
                  {currentUser.firstName + " " + currentUser.lastName}
                </span>
                <span className="header__options--user header__options--user__invisible">
                  SIGN OUT
                </span>
              </div>
            </div>
          ) : (
            <Link
              className="header__options--option header__options--option--SignIn"
              onClick={toggleUserSignInHidden}
            >
              <img
                src={SignInIcon}
                alt="SignIn Icon"
                className="header__icon"
              />
              SIGN IN / REGISTER
            </Link>
          )}

          <CartIcon />
        </div>
      )}

      {currentUser != null ? (userSignInHidden = false) : null}
      {userSignInHidden ? <SignInDropdown /> : null}
      {cartHidden ? null : <CartDropdown />}
    </div>
  );
}

// mapStateToProps + connect() = use anywhere we need properties from rooReducer

// mapStateToProps gives  access to the state(rootReducer)
const mapStateToProps = createStructuredSelector({
  currentUser: selectCurrentUser,
  cartHidden: selectCartHidden,
  userSignInHidden: selectUserSignInHidden,
});

const mapDispatchToProps = (dispatch) => ({
  toggleUserSignInHidden: () => dispatch(toggleUserSignInHidden()),
});

// connect() = function that allows the acces to the state -> rootReducer
export default connect(mapStateToProps, mapDispatchToProps)(Header);
