import RentalActionTypes from "./rental.types";

const INITIAL_STATE = {
  rentals: null,
};

// * reducer are functions which take in a a current state and an action as arguments
// * -> return a new state
const rentalReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case RentalActionTypes.UPDATE_RENTALS:
      return {
        ...state,
        rentals: action.payload,
      };
    default:
      return state;
  }
};

export default rentalReducer;
